import React from 'react'
import axios from 'axios'
import AccountBox from 'material-react-icons/AccountBox'

class Player extends React.Component {
    constructor(props){
        super(props)

        this.player = "hello"
        // this.player = {
        //     "player_id": this.props.value,
        //     "username": "player_one"
        // }
        this.state = {
            error: null,
            isLoaded: false,
            player: {}
          };

    }

    componentDidMount() {
        fetch('https://persistent-hangman.herokuapp.com/players/'+this.props.value)
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                items: result.items
              });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }
    


    render() {
        axios
        .get('https://persistent-hangman.herokuapp.com/players/'+this.props.value)
        .then(resp => {
            console.log("player data",resp.data );
            this.player = resp.data
        })
        return (
            <div className="player">
                <div className="feature-item">
                    <div className="row">
                        <div className="col-sm-6 profile text-muted" >
                            <AccountBox size={64} />
                        </div>
                        <div className="col-sm-6">
                            <h2>  {this.player}, <span className="text-muted"> Id: </span>{this.player}</h2>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Player
