import React from 'react'
import Game from './game'
import Player from './player'


class Page extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          items: []
        };
    }

    componentDidMount() {
        fetch("https://persistent-hangman.herokuapp.com/players/" + this.props.match.params.id + "/games")
          .then(res => res.json())
          .then(
            (result) => {
                console.log(result)
              this.setState({
                isLoaded: true,
                items: result.items
              });
            },
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }
    
    render() {
        return (
            <div className="container">
                
                <Player value={this.props.match.params.id} />
                <div className="move">
                    <div className="row">
                        <Game />
                    </div>
                </div>
            </div>
        )
    }

    render() {
        const { error, isLoaded, games } = this.state;
        if (error) {
          return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          return (
            <ul>
              {/* {items.map(item => (
                <li key={item.name}>
                  {item.name} {item.price}
                </li>
              ))} */}
            </ul>

            // <div className="container">
                            
            // <Player value={this.props.match.params.id} />
            // <div className="move">
            //     <div className="row">
            //         <Game />
            //     </div>
            // </div>
            // </div>


          );
        }
      }
    



}

export default Page
