import React from 'react'
import AccountBox from 'material-react-icons/AccountBox'

class Player extends React.Component {
    constructor(props){
        super(props)

        this.player = "hello"
        // this.player = {
        //     "player_id": this.props.value,
        //     "username": "player_one"
        // }
        this.state = {
            error: null,
            isLoaded: false,
            player: {}
          };

    }

    componentDidMount() {
        fetch('https://persistent-hangman.herokuapp.com/players/'+this.props.value)
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                player: result.data
              });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
      }
    


    render() {

        const { error, isLoaded, player } = this.state;
        if (error) {
        return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
        return <div>Loading...</div>;
        } else {
        return (
            <div className="player">
                <div className="feature-item">
                    <div className="row">
                        <div className="col-sm-6 profile text-muted" >
                            <AccountBox size={64} />
                        </div>
                        <div className="col-sm-6">
                            <h2>  {player}, <span className="text-muted"> Id: </span>{player}</h2>
                        </div>
                    </div>
                </div>
            </div>
        );
        }
    }
}

export default Player
