import React from 'react'
import { Redirect } from 'react-router-dom'


class Home extends React.Component {

    constructor(props) {
        super(props);
        this.state = {redirect: false,
                      value: ''};
    
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }


    state = {
        redirect: false,

      }
      setRedirect = () => {
        this.setState({
          redirect: true
        })
      }
      renderRedirect = () => {
        if (this.state.redirect) {
          return <Redirect to='/target' />
        }
      }


    render() {
        return (
            <div className="container move">
                <div className="login-form">
                        <div className="panel">
                            <h2>Player Login</h2>
                            <p>Please enter your player id</p>
                        </div>
                        <form>
                            <div className="form-group">
                                <input value={this.state.inputValue} type="text" className="form-control" id="inputId" placeholder="Player id"/>
                            </div>
                            {this.renderRedirect()}
                            <button type="button" onClick={this.setRedirect} className="btn btn-primary">Login</button>
                        </form>
                </div>
            </div>
        )
    }
}

export default Home
