import React from 'react'
import { Redirect } from 'react-router-dom'


class Home extends React.Component {

    constructor(props) {
        super(props);
        this.state = {username: '', redirectTo: false };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleClick = this.handleClick.bind(this)
      }
    
      handleChange(event) {
        this.setState({username: event.target.value});
      }
    
      handleSubmit(event) {
        event.preventDefault();
        this.setState({redirectTo: true });
      }
      
      handleClick () {
        axios.get("https://persistent-hangman.herokuapp.com/players/" + this.props.match.params.id + "/games/start")
          .then(response => 
                this.fetchData())
      }

    render() {
        let { redirectTo } = this.state;
        if (redirectTo) return <Redirect to={'/'} />;

        return (
            <div className="container move">
                <div className="login-form">
                        <div className="panel">
                            <h2>Player Sign up</h2>
                            <p>Please enter your username</p>
                        </div>
                        <form onSubmit={this.handleSubmit}>
                            <div className="form-group">
                                <input value={this.state.value} onChange={this.handleChange} type="text" className="form-control" id="inputId" placeholder="Player id"/>
                            </div>
                            <p>
                                <br></br>
                            </p>
                            <button type="submit" className="btn btn-primary">Sign Up</button>
                        </form>
                            <button className="btn btn-primary">Sign Up</button>
                </div>
            </div>
        )
    }
}

export default Home
