import React from 'react'
import Game from './game'
import Player from './player'
import axios from 'axios'
// import { Redirect } from 'react-router-dom' 

class Page extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          games: []
        //   redirectTo: false 
        };
        this.handleClick = this.handleClick.bind(this)
    }

    handleClick () {
        axios.get("https://persistent-hangman.herokuapp.com/players/" + this.props.match.params.id + "/games/start")
          .then(response => 
                this.fetchData())
        // this.setState({redirectTo: true, games: [] });
        this.setState({games: [""], isLoaded: false});
      }

    componentDidMount() {
        this.fetchData();
      }

    fetchData = () => {
        fetch("https://persistent-hangman.herokuapp.com/players/" + this.props.match.params.id + "/games")
          .then(res => res.json())
          .then(
            (result) => {
            console.log("fetch complete");
            console.log(this.state.games);
              this.setState({
                isLoaded: true,
                games: result
              });
              console.log(this.state.games);
            },
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
    }
    
    render() {
        const { error, isLoaded, games } = this.state;
        //let { redirectTo } = this.state;
        //if (redirectTo) return <Redirect to={'/player/'+this.props.match.params.id} />; 
        if (error) {
            return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
            return <div>Loading...</div>;
        } else {
            return (
                <div className="container">
                    <Player value={this.props.match.params.id} />
                    <div className="move">
                        <button className='btn' onClick={this.handleClick}>
                        New Game
                    </button>
                    </div>
                    <div className="move">
                        <div className="row">
                            {games.map(game => (
                                <Game key={game.game_id} value={game}/>
                            ))}
                        </div>
                    </div>
                </div>
            )
        }
    }

}

export default Page
