import React from 'react'
import { Redirect } from 'react-router-dom'


class Home extends React.Component {

    state = {
        redirect: false
      }
      setRedirect = () => {
        this.setState({
          redirect: true
        })
      }
      renderRedirect = () => {
        if (this.state.redirect) {
          return <Redirect to='/target' />
        }
      }


    render() {
        return (
            <div className="container move">
                <div className="login-form">
                        <div className="panel">
                            <h2>Player Login</h2>
                            <p>Please enter your player id</p>
                        </div>
                        <form>
                            <div className="form-group">
                                <input type="text" className="form-control" id="inputId" placeholder="Player id"/>
                            </div>
                            {this.renderRedirect()}
                            <button type="submit" onClick={this.setRedirect} className="btn btn-primary">Login</button>
                        </form>
                </div>
            </div>
        )
    }
}

export default Home
